// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Permit.sol";
import "@openzeppelin/contracts/access/Ownable2Step.sol";

import "./interfaces/IAvUSDDefinitions.sol";

/**
 * @title avUSD
 * @notice Stable Coin Contract
 * @dev Only approved minters can mint new tokens
 */
contract AvUSD is Ownable2Step, ERC20Burnable, ERC20Permit, IAvUSDDefinitions {

  mapping(address => bool) public minters;

  constructor(address admin) ERC20("avUSD", "avUSD") ERC20Permit("avUSD") Ownable(admin) {
    /// @dev zero address will be checked on the Ownable constructor
    // if (admin == address(0)) revert ZeroAddressException();
    /// @dev Ownable constructor will assign initial ownership
    // _transferOwnership(admin);
  }

  function setMinter(address minter, bool isMinter) external onlyOwner {
    emit MinterUpdated(minter, isMinter);
    minters[minter] = isMinter;
  }

  function mint(address to, uint256 amount) external {
    if (!minters[msg.sender]) revert OnlyMinter();
    _mint(to, amount);
  }

  function renounceOwnership() public view override onlyOwner {
    revert CannotRenounceOwnership();
  }
}

contracts/AvUSDBridging.sol.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.20;

import {Client} from '@chainlink/contracts-ccip/src/v0.8/ccip/libraries/Client.sol';
import {IAny2EVMMessageReceiver} from '@chainlink/contracts-ccip/src/v0.8/ccip/interfaces/IAny2EVMMessageReceiver.sol';
import {IRouterClient} from '@chainlink/contracts-ccip/src/v0.8/ccip/interfaces/IRouterClient.sol';

import {OApp, Origin, MessagingFee} from '@layerzerolabs/lz-evm-oapp-v2/oapp/contracts/oapp/OApp.sol';
import {MessagingReceipt} from '@layerzerolabs/lz-evm-protocol-v2/contracts/interfaces/ILayerZeroEndpointV2.sol';

import {IERC20} from '@openzeppelin/contracts/interfaces/IERC20.sol';
import {IERC165} from '@openzeppelin/contracts/utils/introspection/IERC165.sol';
import {Ownable} from '@openzeppelin/contracts/access/Ownable.sol';
import {ReentrancyGuard} from '@openzeppelin/contracts/utils/ReentrancyGuard.sol';
import {SafeERC20} from '@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol';

import {IAvUSD} from './interfaces/IAvUSD.sol';
import {IStakedAvUSDV3} from './interfaces/IStakedAvUSDV3.sol';

contract AvUSDBridging is
  ReentrancyGuard, //                                            Bridge (send) function protection
  OApp, //                                                       LayerZero contract structure
  IAny2EVMMessageReceiver, // ─────────────────────────────────╮ CCIP message receiver interface
  IERC165 // ──────────────────────────────────────────────────╯ CCIP introspection for the message receiver
{
  using SafeERC20 for IERC20;
  // ┌─────────────────────────────────────────────────────────────┐
  // | Events                                                      |
  // └─────────────────────────────────────────────────────────────┘

  event CCIPMessageReceived(
    bytes32 messageId,
    uint64 sourceChainSelector,
    address tokenSender,
    address tokenReceiver,
    uint256 tokenAmount,
    bool isStaked
  );
  event CCIPMessageSent(
    bytes32 messageId,
    uint64 destinationChainSelector,
    address tokenSender,
    address tokenReceiver,
    uint256 tokenAmount,
    bool isStaked
  );
  event CCIPPeerUpdated(uint64 chainSelector, address peer);
  event CCIPRouterUpdated(address newCCIPRouter);

  event LZMessageReceived(
    bytes32 guid,
    uint32 srcEid,
    address tokenSender,
    address tokenReceiver,
    uint256 tokenAmount,
    bool isStaked
  );
  event LZMessageSent(
    bytes32 guid,
    uint32 dstEid,
    address tokenSender,
    address tokenReceiver,
    uint256 tokenAmount,
    bool isStaked
  );

  // ┌─────────────────────────────────────────────────────────────┐
  // | Custom errors                                               |
  // └─────────────────────────────────────────────────────────────┘

  error InvalidParamError();
  error NotAuthorizedError();

  // ┌─────────────────────────────────────────────────────────────┐
  // | State                                                       |
  // └─────────────────────────────────────────────────────────────┘

  address public ccipRouter; // ───────────────────────────────╮ address for Chainlink's CCIP router on the current chain
  mapping(uint64 => address) public ccipWhitelistedPeers; // ──╯ CCIP Chain Selector => Address on the peer chain

  IAvUSD public immutable avUsd; // ───────────────────────────╮ avUSD stablecoin
  IStakedAvUSDV3 public immutable savUsd; // ──────────────────╯ staked avUSD vault

  // ┌─────────────────────────────────────────────────────────────┐
  // | Constructor                                                 |
  // └─────────────────────────────────────────────────────────────┘

  /**
   * @notice Constructor to initialize the AvUSDBridging contract.
   * @param _avUsd Address of the avUSD token contract.
   * @param _savUsd Address of the savUSD (staked avUSD) vault contract.
   * @param _lzEndpoint LayerZero endpoint for message sending/receiving.
   * @param _ccipRouter Chainlink's CCIP router address for message handling.
   * @param _owner Address of the contract owner.
   * @dev Initializes the contract, sets up the LayerZero and CCIP routers, and approves the staked avUSD vault to manage avUSD tokens.
   */
  constructor(
    address _avUsd,
    address _savUsd,
    address _lzEndpoint, /// @dev immutable, if zero will permanently disable LayerZero bridging
    address _ccipRouter, /// @dev can be set to zero at the start but modified later on
    address _owner // zero address check will be performed by the Owner constructor
  ) OApp(_lzEndpoint, _owner) Ownable(_owner) {
    if (_avUsd == address(0) || _savUsd == address(0)) {
      revert InvalidParamError();
    }
    avUsd = IAvUSD(_avUsd);
    savUsd = IStakedAvUSDV3(_savUsd);
    ccipRouter = _ccipRouter;
    /// @dev allowance for StakedAvUSD to be able to capture AvUSD when depositing
    avUsd.approve(_savUsd, type(uint256).max);
  }

  // ┌─────────────────────────────────────────────────────────────┐
  // | Admin functions                                             |
  // └─────────────────────────────────────────────────────────────┘

  /**
   * @notice Sets a whitelisted peer for a specified chain using its chain selector.
   * @param _chainSelector The chain selector for which the peer is being set.
   * @param _peer The address of the whitelisted peer on the destination chain.
   * @dev Only callable by the contract owner. Allows for cross-chain messages to/from the whitelisted peer.
   * @dev Zero address is allowed (which becomes an 'unset').
   */
  function setCCIPWhitelistedPeer(uint64 _chainSelector, address _peer) external onlyOwner {
    ccipWhitelistedPeers[_chainSelector] = _peer;
    emit CCIPPeerUpdated(_chainSelector, _peer);
  }

  /**
   * @notice Sets the CCIP router address.
   * @param _ccipRouter The new address of the CCIP router.
   * @dev Only callable by the contract owner. Setting to zero disables CCIP bridging.
   * @dev Zero address is allowed (which becomes an 'unset').
   */
  function setCCIPRouter(address _ccipRouter) external onlyOwner {
    ccipRouter = _ccipRouter;
    emit CCIPRouterUpdated(_ccipRouter);
  }

  // ┌─────────────────────────────────────────────────────────────┐
  // | Layerzero functions                                         |
  // └─────────────────────────────────────────────────────────────┘

  /**
   * @notice Quotes the fee for sending a message with LayerZero.
   * @param _dstEid The destination chain's LayerZero endpoint ID.
   * @param _tokenReceiver The receiver's address on the destination chain.
   * @param _tokenAmount The amount of tokens being transferred.
   * @param _isStaked A boolean indicating if the tokens are staked or unstaked.
   * @param _options Additional LayerZero message options (e.g., gas limit).
   * @return The quoted fee in the native token of the current chain.
   * @dev This function calculates the fee required to send a message to a LayerZero endpoint.
   * @dev The fee amount is calculated in the native token of the current chain.
   */
  function quoteSendFeeWithLayerzero(
    uint32 _dstEid,
    address _tokenReceiver,
    uint256 _tokenAmount,
    bool _isStaked,
    bytes memory _options
  ) external view returns (uint256) {
    if (address(endpoint) == address(0)) {
      revert NotAuthorizedError();
    }
    bool _payInLzToken = false;
    bytes memory _message = abi.encode(msg.sender, _tokenReceiver, _tokenAmount, _isStaked);
    MessagingFee memory _fee = _quote(_dstEid, _message, _options, _payInLzToken);
    return _fee.nativeFee;
  }

  /**
   * @notice Sends tokens across chains using LayerZero protocol.
   * @param _dstEid The destination chain's LayerZero endpoint ID.
   * @param _tokenReceiver The receiver's address on the destination chain.
   * @param _tokenAmount The amount of tokens being transferred.
   * @param _isStaked A boolean indicating if the tokens are staked or unstaked.
   * @param _options Additional LayerZero message options (e.g., gas limit).
   * @dev Transfers tokens across chains, burns them on the current chain, and emits a LayerZero message event.
   */
  function sendWithLayerzero(
    uint32 _dstEid,
    address _tokenReceiver,
    uint256 _tokenAmount,
    bool _isStaked,
    bytes calldata _options
  ) external payable nonReentrant {
    if (address(endpoint) == address(0)) {
      revert NotAuthorizedError();
    }

    _tokenAmount = _transferFromAndBurnTokens(_tokenAmount, _isStaked);

    bytes memory _message = abi.encode(msg.sender, _tokenReceiver, _tokenAmount, _isStaked);

    MessagingReceipt memory _receipt = _lzSend(
      _dstEid,
      _message,
      _options,
      // Fee in native gas and ZRO token
      MessagingFee(msg.value, 0),
      // Refund address in case of failed source message
      payable(msg.sender)
    );

    emit LZMessageSent(_receipt.guid, _dstEid, msg.sender, _tokenReceiver, _tokenAmount, _isStaked);
  }

  /**
   * @notice Receives messages sent via the LayerZero protocol.
   * @param _origin Information about the origin chain.
   * @param _guid Unique identifier for tracking the message.
   * @param _payload Encoded payload of the message.
   * @dev Decodes the payload, processes the token minting, and emits an event.
   */
  function _lzReceive(
    Origin calldata _origin,
    bytes32 _guid,
    bytes calldata _payload,
    address, // Executor address as specified by the OApp.
    bytes calldata // Any extra data or options to trigger on receipt.
  ) internal override {
    (address _tokenSender, address _tokenReceiver, uint256 _tokenAmount, bool _isStaked) = abi.decode(
      _payload,
      (address, address, uint256, bool)
    );

    emit LZMessageReceived(_guid, _origin.srcEid, _tokenSender, _tokenReceiver, _tokenAmount, _isStaked);

    _mintWithOptionalStake(_tokenReceiver, _tokenAmount, _isStaked);
  }

  // ┌─────────────────────────────────────────────────────────────┐
  // | CCIP functions                                              |
  // └─────────────────────────────────────────────────────────────┘

  /**
   * @notice Quotes the fee required to send a message via CCIP.
   * @param _destinationChainSelector The selector for the destination chain.
   * @param _tokenReceiver The recipient address on the destination chain.
   * @param _tokenAmount The amount of tokens being sent.
   * @param _isStaked Whether the tokens are staked or not.
   * @return The fee in the native token required for sending the message.
   * @dev The fee amount is calculated in the native token of the current chain.
   */
  function quoteSendFeeWithCCIP(
    uint64 _destinationChainSelector,
    address _tokenReceiver,
    uint256 _tokenAmount,
    bool _isStaked
  ) external view returns (uint256) {
    if (ccipRouter == address(0)) {
      revert NotAuthorizedError();
    }
    Client.EVM2AnyMessage memory _message = _createCCIPMessage(
      _destinationChainSelector,
      _tokenReceiver,
      _tokenAmount,
      _isStaked
    );
    return IRouterClient(ccipRouter).getFee(_destinationChainSelector, _message);
  }

  /**
   * @notice Sends tokens across chains using Chainlink CCIP protocol.
   * @param _destinationChainSelector The chain selector for the destination chain.
   * @param _tokenReceiver The receiver's address on the destination chain.
   * @param _tokenAmount The amount of tokens being transferred.
   * @param _isStaked A boolean indicating if the tokens are staked or unstaked.
   * @dev Transfers tokens across chains, burns them on the current chain, and emits a CCIP message event.
   */
  function sendWithCCIP(
    uint64 _destinationChainSelector,
    address _tokenReceiver,
    uint256 _tokenAmount,
    bool _isStaked
  ) external payable nonReentrant {
    _tokenAmount = _transferFromAndBurnTokens(_tokenAmount, _isStaked);

    Client.EVM2AnyMessage memory _message = _createCCIPMessage(
      _destinationChainSelector,
      _tokenReceiver,
      _tokenAmount,
      _isStaked
    );

    IRouterClient _router = IRouterClient(ccipRouter);
    uint256 _fee = _router.getFee(_destinationChainSelector, _message);
    /// @dev fees don't change often enough to necessitate the implementation of a refund mechanism, so the contract can expect the exact amount to be sent
    if (_fee != msg.value) {
      revert InvalidParamError();
    }

    bytes32 _messageId = _router.ccipSend{value: _fee}(_destinationChainSelector, _message);

    emit CCIPMessageSent(_messageId, _destinationChainSelector, msg.sender, _tokenReceiver, _tokenAmount, _isStaked);
  }

  /**
   * @inheritdoc IAny2EVMMessageReceiver
   * @notice Receives messages sent via the Chainlink CCIP protocol.
   * @param _message Information about the received message.
   * @dev Verifies the sender, decodes the payload, processes the token minting, and emits an event.
   */
  function ccipReceive(Client.Any2EVMMessage calldata _message) external override {
    if (msg.sender != ccipRouter) {
      revert NotAuthorizedError();
    }
    address _messageSender = abi.decode(_message.sender, (address));
    if (ccipWhitelistedPeers[_message.sourceChainSelector] != _messageSender) {
      revert NotAuthorizedError();
    }
    (address _tokenSender, address _tokenReceiver, uint256 _tokenAmount, bool _isStaked) = abi.decode(
      _message.data,
      (address, address, uint256, bool)
    );

    emit CCIPMessageReceived(
      _message.messageId,
      _message.sourceChainSelector,
      _tokenSender,
      _tokenReceiver,
      _tokenAmount,
      _isStaked
    );

    _mintWithOptionalStake(_tokenReceiver, _tokenAmount, _isStaked);
  }

  /**
   * @notice Creates a CCIP message for token transfer.
   * @param _destinationChainSelector The chain selector for the destination chain.
   * @param _tokenReceiver The receiver's address on the destination chain.
   * @param _tokenAmount The amount of tokens being transferred.
   * @param _isStaked A boolean indicating if the tokens are staked or unstaked.
   * @return A CCIP message with the encoded transfer details.
   * @dev This function builds the message to be sent across chains using CCIP.
   */
  function _createCCIPMessage(
    uint64 _destinationChainSelector,
    address _tokenReceiver,
    uint256 _tokenAmount,
    bool _isStaked
  ) internal view returns (Client.EVM2AnyMessage memory) {
    address _peer = ccipWhitelistedPeers[_destinationChainSelector];
    if (_peer == address(0)) {
      revert NotAuthorizedError();
    }
    Client.EVMTokenAmount[] memory _tokensToSendDetails = new Client.EVMTokenAmount[](0);
    return
      Client.EVM2AnyMessage({
        receiver: abi.encode(_peer),
        data: abi.encode(msg.sender, _tokenReceiver, _tokenAmount, _isStaked),
        tokenAmounts: _tokensToSendDetails,
        extraArgs: '',
        feeToken: address(0)
      });
  }

  // ┌─────────────────────────────────────────────────────────────┐
  // | Internal Utility Functions                                  |
  // └─────────────────────────────────────────────────────────────┘

  /**
   * @notice Transfers and burns tokens before cross-chain messaging.
   * @param _tokenAmount The amount of tokens to transfer and burn.
   * @param _isStaked A boolean indicating if the tokens are staked or unstaked.
   * @return The actual amount of tokens transferred and burned.
   * @dev Handles the token transfer logic and burns them for cross-chain transfers.
   */
  function _transferFromAndBurnTokens(uint256 _tokenAmount, bool _isStaked) internal returns (uint256) {
    if (_isStaked) {
      IERC20(savUsd).safeTransferFrom(msg.sender, address(this), _tokenAmount);
      _tokenAmount = savUsd.bridgeRedeem(_tokenAmount);
    } else {
      IERC20(avUsd).safeTransferFrom(msg.sender, address(this), _tokenAmount);
    }
    avUsd.burn(_tokenAmount);
    return _tokenAmount;
  }

  /**
   * @notice Mints tokens to the receiver with optional staking.
   * @param _tokenReceiver The address of the token receiver.
   * @param _tokenAmount The amount of tokens to mint.
   * @param _isStaked A boolean indicating if the tokens should be staked or unstaked.
   * @dev If `_isStaked` is true, the tokens are staked in the savUSD vault.
   */
  function _mintWithOptionalStake(address _tokenReceiver, uint256 _tokenAmount, bool _isStaked) internal {
    if (_isStaked) {
      avUsd.mint(address(this), _tokenAmount);
      savUsd.deposit(_tokenAmount, _tokenReceiver);
    } else {
      avUsd.mint(_tokenReceiver, _tokenAmount);
    }
  }

  /// @dev Indicates that this contract implements IAny2EVMMessageReceiver
  function supportsInterface(bytes4 _interfaceId) public pure virtual override returns (bool) {
    return _interfaceId == type(IAny2EVMMessageReceiver).interfaceId || _interfaceId == type(IERC165).interfaceId;
  }
}

contracts/AvUSDMinting.sol.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.20;

/* solhint-disable private-vars-leading-underscore */
/* solhint-disable var-name-mixedcase */

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/utils/cryptography/MessageHashUtils.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";

import "./SingleAdminAccessControl.sol";
import "./interfaces/IAvUSDMinting.sol";
import "./interfaces/IWAVAX.sol";
import "./interfaces/IAvUSD.sol";

/**
 * @title AvUSD Minting
 * @notice This contract mints and redeems avUSD
 */
contract AvUSDMinting is IAvUSDMinting, SingleAdminAccessControl, ReentrancyGuard {
  using SafeERC20 for IERC20;
  using EnumerableSet for EnumerableSet.AddressSet;

  /* --------------- CONSTANTS --------------- */

  /// @notice EIP712 domain
  bytes32 private constant EIP712_DOMAIN =
    keccak256("EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)");

  /// @notice route type
  bytes32 private constant ROUTE_TYPE = keccak256("Route(address[] addresses,uint256[] ratios)");

  /// @notice order type
  bytes32 private constant ORDER_TYPE = keccak256(
    "Order(uint8 order_type,uint256 expiry,uint256 nonce,address benefactor,address beneficiary,address collateral_asset,uint256 collateral_amount,uint256 avusd_amount)"
  );

  /// @notice role enabling to invoke mint
  bytes32 private constant MINTER_ROLE = keccak256("MINTER_ROLE");

  /// @notice role enabling to invoke redeem
  bytes32 private constant REDEEMER_ROLE = keccak256("REDEEMER_ROLE");

  /// @notice role enabling to transfer collateral to custody wallets
  bytes32 private constant COLLATERAL_MANAGER_ROLE = keccak256("COLLATERAL_MANAGER_ROLE");

  /// @notice role enabling to disable mint and redeem and remove minters and redeemers in an emergency
  bytes32 private constant GATEKEEPER_ROLE = keccak256("GATEKEEPER_ROLE");

  /// @notice EIP712 domain hash
  bytes32 private constant EIP712_DOMAIN_TYPEHASH = keccak256(abi.encodePacked(EIP712_DOMAIN));

  /// @notice address denoting native ether
  address private constant NATIVE_TOKEN = 0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE;

  /// @notice EIP712 name
  bytes32 private constant EIP_712_NAME = keccak256("AvUSDMinting");

  /// @notice holds EIP712 revision
  bytes32 private constant EIP712_REVISION = keccak256("1");

  /// @notice required ratio for route
  uint256 private constant ROUTE_REQUIRED_RATIO = 100_00; // 100%

  IWAVAX private immutable WAVAX;

  /* --------------- STATE VARIABLES --------------- */

  /// @notice avusd stablecoin
  IAvUSD public immutable avusd;

  /// @notice Supported assets
  EnumerableSet.AddressSet internal _supportedAssets;

  // @notice custodian addresses
  EnumerableSet.AddressSet internal _custodianAddresses;

  /// @notice holds computable chain id
  uint256 private immutable _chainId;

  /// @notice holds computable domain separator
  bytes32 private immutable _domainSeparator;

  /// @notice user deduplication
  mapping(address => mapping(uint256 => uint256)) private _orderBitmaps;

  /// @notice avUSD minted per block
  mapping(uint256 => uint256) public mintedPerBlock;
  /// @notice avUSD redeemed per block
  mapping(uint256 => uint256) public redeemedPerBlock;

  /// @notice For smart contracts to delegate signing to EOA address
  mapping(address => mapping(address => DelegatedSignerStatus)) public delegatedSigner;

  /// @notice max minted avUSD allowed per block
  uint256 public maxMintPerBlock;
  /// @notice max redeemed avUSD allowed per block
  uint256 public maxRedeemPerBlock;

  /* --------------- MODIFIERS --------------- */

  /// @notice ensure that the already minted avUSD in the actual block plus the amount to be minted is below the maxMintPerBlock var
  /// @param mintAmount The avUSD amount to be minted
  modifier belowMaxMintPerBlock(uint256 mintAmount) {
    if (mintedPerBlock[block.number] + mintAmount > maxMintPerBlock) revert MaxMintPerBlockExceeded();
    // Add to the minted amount in this block
    mintedPerBlock[block.number] += mintAmount;
    _;
  }

  /// @notice ensure that the already redeemed avUSD in the actual block plus the amount to be redeemed is below the maxRedeemPerBlock var
  /// @param redeemAmount The avUSD amount to be redeemed
  modifier belowMaxRedeemPerBlock(uint256 redeemAmount) {
    if (redeemedPerBlock[block.number] + redeemAmount > maxRedeemPerBlock) revert MaxRedeemPerBlockExceeded();
    // Add to the redeemed amount in this block
    redeemedPerBlock[block.number] += redeemAmount;
    _;
  }

  /* --------------- CONSTRUCTOR --------------- */

  constructor(
    IAvUSD _avusd,
    IWAVAX _wavax,
    address[] memory _assets,
    address[] memory _custodians,
    address _admin,
    uint256 _maxMintPerBlock,
    uint256 _maxRedeemPerBlock
  ) {
    if (address(_avusd) == address(0)) revert InvalidAvUSDAddress();
    if (address(_wavax) == address(0)) revert InvalidZeroAddress();
    if (_assets.length == 0) revert NoAssetsProvided();
    if (_admin == address(0)) revert InvalidZeroAddress();
    avusd = _avusd;
    WAVAX = _wavax;

    _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);

    for (uint256 i = 0; i < _assets.length;) {
      addSupportedAsset(_assets[i]);
      unchecked {
        ++i;
      }
    }

    for (uint256 j = 0; j < _custodians.length;) {
      addCustodianAddress(_custodians[j]);
      unchecked {
        ++j;
      }
    }

    // Set the max mint/redeem limits per block
    _setMaxMintPerBlock(_maxMintPerBlock);
    _setMaxRedeemPerBlock(_maxRedeemPerBlock);

    if (msg.sender != _admin) {
      _grantRole(DEFAULT_ADMIN_ROLE, _admin);
    }

    _chainId = block.chainid;
    _domainSeparator = _computeDomainSeparator();

    emit AvUSDSet(address(_avusd));
  }

  /* --------------- EXTERNAL --------------- */

  /**
   * @notice Fallback function to receive ether
   */
  receive() external payable {
    emit Received(msg.sender, msg.value);
  }

  /**
   * @notice Mint stablecoins from assets
   * @param order struct containing order details and confirmation from server
   * @param signature signature of the taker
   */
  function mint(Order calldata order, Route calldata route, Signature calldata signature)
    external
    override
    nonReentrant
    onlyRole(MINTER_ROLE)
    belowMaxMintPerBlock(order.avusd_amount)
  {
    if (order.order_type != OrderType.MINT) revert InvalidOrder();
    verifyOrder(order, signature);
    if (!verifyRoute(route)) revert InvalidRoute();
    _deduplicateOrder(order.benefactor, order.nonce);

    if (order.collateral_asset == address(WAVAX)) {
      _transferEthCollateral(
        order.collateral_amount, order.collateral_asset, order.benefactor, route.addresses, route.ratios
      );
    } else {
      _transferCollateral(
        order.collateral_amount, order.collateral_asset, order.benefactor, route.addresses, route.ratios
      );
    }
    avusd.mint(order.beneficiary, order.avusd_amount);
    emit Mint(
      msg.sender,
      order.benefactor,
      order.beneficiary,
      order.collateral_asset,
      order.collateral_amount,
      order.avusd_amount
    );
  }

  /**
   * @notice Redeem stablecoins for assets
   * @param order struct containing order details and confirmation from server
   * @param signature signature of the taker
   */
  function redeem(Order calldata order, Signature calldata signature)
    external
    override
    nonReentrant
    onlyRole(REDEEMER_ROLE)
    belowMaxRedeemPerBlock(order.avusd_amount)
  {
    if (order.order_type != OrderType.REDEEM) revert InvalidOrder();
    verifyOrder(order, signature);
    _deduplicateOrder(order.benefactor, order.nonce);
    avusd.burnFrom(order.benefactor, order.avusd_amount);
    _transferToBeneficiary(order.beneficiary, order.collateral_asset, order.collateral_amount);
    emit Redeem(
      msg.sender,
      order.benefactor,
      order.beneficiary,
      order.collateral_asset,
      order.collateral_amount,
      order.avusd_amount
    );
  }

  /// @notice Sets the max mintPerBlock limit
  function setMaxMintPerBlock(uint256 _maxMintPerBlock) external onlyRole(DEFAULT_ADMIN_ROLE) {
    _setMaxMintPerBlock(_maxMintPerBlock);
  }

  /// @notice Sets the max redeemPerBlock limit
  function setMaxRedeemPerBlock(uint256 _maxRedeemPerBlock) external onlyRole(DEFAULT_ADMIN_ROLE) {
    _setMaxRedeemPerBlock(_maxRedeemPerBlock);
  }

  /// @notice Disables the mint and redeem
  function disableMintRedeem() external onlyRole(GATEKEEPER_ROLE) {
    _setMaxMintPerBlock(0);
    _setMaxRedeemPerBlock(0);
  }

  /// @notice Enables smart contracts to delegate an address for signing
  function setDelegatedSigner(address _delegateTo) external {
    delegatedSigner[_delegateTo][msg.sender] = DelegatedSignerStatus.PENDING;
    emit DelegatedSignerInitiated(_delegateTo, msg.sender);
  }

  /// @notice The delegated address to confirm delegation
  function confirmDelegatedSigner(address _delegatedBy) external {
    mapping(address => DelegatedSignerStatus) storage delegatedStatus = delegatedSigner[msg.sender];
    if (delegatedStatus[_delegatedBy] != DelegatedSignerStatus.PENDING) {
      revert DelegationNotInitiated();
    }
    delegatedStatus[_delegatedBy] = DelegatedSignerStatus.ACCEPTED;
    emit DelegatedSignerAdded(msg.sender, _delegatedBy);
  }

  /// @notice Enables smart contracts to undelegate an address for signing
  function removeDelegatedSigner(address _removedSigner) external {
    delegatedSigner[_removedSigner][msg.sender] = DelegatedSignerStatus.REJECTED;
    emit DelegatedSignerRemoved(_removedSigner, msg.sender);
  }

  /// @notice transfers an asset to a custody wallet
  function transferToCustody(address wallet, address asset, uint256 amount)
    external
    nonReentrant
    onlyRole(COLLATERAL_MANAGER_ROLE)
  {
    if (wallet == address(0) || !_custodianAddresses.contains(wallet)) revert InvalidAddress();
    if (asset == NATIVE_TOKEN) {
      (bool success,) = wallet.call{value: amount}("");
      if (!success) revert TransferFailed();
    } else {
      IERC20(asset).safeTransfer(wallet, amount);
    }
    emit CustodyTransfer(wallet, asset, amount);
  }

  /// @notice Removes an asset from the supported assets list
  function removeSupportedAsset(address asset) external onlyRole(DEFAULT_ADMIN_ROLE) {
    if (!_supportedAssets.remove(asset)) revert InvalidAssetAddress();
    emit AssetRemoved(asset);
  }

  /// @notice Checks if an asset is supported.
  function isSupportedAsset(address asset) external view returns (bool) {
    return _supportedAssets.contains(asset);
  }

  /// @notice Removes an custodian from the custodian address list
  function removeCustodianAddress(address custodian) external onlyRole(DEFAULT_ADMIN_ROLE) {
    if (!_custodianAddresses.remove(custodian)) revert InvalidCustodianAddress();
    emit CustodianAddressRemoved(custodian);
  }

  /// @notice Removes the minter role from an account, this can ONLY be executed by the gatekeeper role
  /// @param minter The address to remove the minter role from
  function removeMinterRole(address minter) external onlyRole(GATEKEEPER_ROLE) {
    _revokeRole(MINTER_ROLE, minter);
  }

  /// @notice Removes the redeemer role from an account, this can ONLY be executed by the gatekeeper role
  /// @param redeemer The address to remove the redeemer role from
  function removeRedeemerRole(address redeemer) external onlyRole(GATEKEEPER_ROLE) {
    _revokeRole(REDEEMER_ROLE, redeemer);
  }

  /// @notice Removes the collateral manager role from an account, this can ONLY be executed by the gatekeeper role
  /// @param collateralManager The address to remove the collateralManager role from
  function removeCollateralManagerRole(address collateralManager) external onlyRole(GATEKEEPER_ROLE) {
    _revokeRole(COLLATERAL_MANAGER_ROLE, collateralManager);
  }

  /* --------------- PUBLIC --------------- */

  /// @notice Adds an asset to the supported assets list.
  function addSupportedAsset(address asset) public onlyRole(DEFAULT_ADMIN_ROLE) {
    if (asset == address(0) || asset == address(avusd) || !_supportedAssets.add(asset)) {
      revert InvalidAssetAddress();
    }
    emit AssetAdded(asset);
  }

  /// @notice Adds an custodian to the supported custodians list.
  function addCustodianAddress(address custodian) public onlyRole(DEFAULT_ADMIN_ROLE) {
    if (custodian == address(0) || custodian == address(avusd) || !_custodianAddresses.add(custodian)) {
      revert InvalidCustodianAddress();
    }
    emit CustodianAddressAdded(custodian);
  }

  /// @notice Get the domain separator for the token
  /// @dev Return cached value if chainId matches cache, otherwise recomputes separator, to prevent replay attack across forks
  /// @return The domain separator of the token at current chain
  function getDomainSeparator() public view returns (bytes32) {
    if (block.chainid == _chainId) {
      return _domainSeparator;
    }
    return _computeDomainSeparator();
  }

  /// @notice hash an Order struct
  function hashOrder(Order calldata order) public view override returns (bytes32) {
    return MessageHashUtils.toTypedDataHash(getDomainSeparator(), keccak256(encodeOrder(order)));
  }

  function encodeOrder(Order calldata order) public pure returns (bytes memory) {
    return abi.encode(
      ORDER_TYPE,
      order.order_type,
      order.expiry,
      order.nonce,
      order.benefactor,
      order.beneficiary,
      order.collateral_asset,
      order.collateral_amount,
      order.avusd_amount
    );
  }

  /// @notice assert validity of signed order
  function verifyOrder(Order calldata order, Signature calldata signature)
    public
    view
    override
    returns (bytes32 taker_order_hash)
  {
    taker_order_hash = hashOrder(order);
    address signer = ECDSA.recover(taker_order_hash, signature.signature_bytes);
    if (signer != order.benefactor && delegatedSigner[signer][order.benefactor] != DelegatedSignerStatus.ACCEPTED) {
      revert InvalidSignature();
    }
    if (order.beneficiary == address(0)) revert InvalidAddress();
    if (order.collateral_amount == 0) revert InvalidAmount();
    if (order.avusd_amount == 0) revert InvalidAmount();
    if (block.timestamp > order.expiry) revert SignatureExpired();
  }

  /// @notice assert validity of route object per type
  function verifyRoute(Route calldata route) public view override returns (bool) {
    uint256 totalRatio = 0;
    if (route.addresses.length != route.ratios.length) {
      return false;
    }
    if (route.addresses.length == 0) {
      return false;
    }
    for (uint256 i = 0; i < route.addresses.length;) {
      if (!_custodianAddresses.contains(route.addresses[i]) || route.addresses[i] == address(0) || route.ratios[i] == 0)
      {
        return false;
      }
      totalRatio += route.ratios[i];
      unchecked {
        ++i;
      }
    }
    return totalRatio == ROUTE_REQUIRED_RATIO;
  }

  /// @notice verify validity of nonce by checking its presence
  function verifyNonce(address sender, uint256 nonce) public view override returns (uint256, uint256, uint256) {
    if (nonce == 0 || nonce > type(uint64).max) revert InvalidNonce();
    uint256 invalidatorSlot = uint64(nonce) >> 8;
    uint256 invalidatorBit = 1 << uint8(nonce);
    uint256 invalidator = _orderBitmaps[sender][invalidatorSlot];
    if (invalidator & invalidatorBit != 0) revert InvalidNonce();

    return (invalidatorSlot, invalidator, invalidatorBit);
  }

  /* --------------- PRIVATE --------------- */

  /// @notice deduplication of taker order
  function _deduplicateOrder(address sender, uint256 nonce) private {
    (uint256 invalidatorSlot, uint256 invalidator, uint256 invalidatorBit) = verifyNonce(sender, nonce);
    _orderBitmaps[sender][invalidatorSlot] = invalidator | invalidatorBit;
  }

  /* --------------- INTERNAL --------------- */

  /// @notice transfer supported asset to beneficiary address
  function _transferToBeneficiary(address beneficiary, address asset, uint256 amount) internal {
    if (asset == NATIVE_TOKEN) {
      if (address(this).balance < amount) revert InvalidAmount();
      (bool success,) = (beneficiary).call{value: amount}("");
      if (!success) revert TransferFailed();
    } else {
      if (!_supportedAssets.contains(asset)) revert UnsupportedAsset();
      IERC20(asset).safeTransfer(beneficiary, amount);
    }
  }

  /// @notice transfer supported asset to array of custody addresses per defined ratio
  function _transferCollateral(
    uint256 amount,
    address asset,
    address benefactor,
    address[] calldata addresses,
    uint256[] calldata ratios
  ) internal {
    // cannot mint using unsupported asset or native ETH even if it is supported for redemptions
    if (!_supportedAssets.contains(asset) || asset == NATIVE_TOKEN) revert UnsupportedAsset();
    IERC20 token = IERC20(asset);
    uint256 totalTransferred = 0;
    for (uint256 i = 0; i < addresses.length - 1;) {
      uint256 amountToTransfer = (amount * ratios[i]) / ROUTE_REQUIRED_RATIO;
      token.safeTransferFrom(benefactor, addresses[i], amountToTransfer);
      totalTransferred += amountToTransfer;
      unchecked {
        ++i;
      }
    }
    uint256 remainingBalance = amount - totalTransferred;
    token.safeTransferFrom(benefactor, addresses[addresses.length - 1], remainingBalance);
  }

  /// @notice transfer supported asset to array of custody addresses per defined ratio
  function _transferEthCollateral(
    uint256 amount,
    address asset,
    address benefactor,
    address[] calldata addresses,
    uint256[] calldata ratios
  ) internal {
    if (!_supportedAssets.contains(asset) || asset != address(WAVAX)) revert UnsupportedAsset();
    IERC20 token = IERC20(asset);
    token.safeTransferFrom(benefactor, address(this), amount);

    // unwrap
    WAVAX.withdraw(amount);

    uint256 totalTransferred = 0;
    for (uint256 i = 0; i < addresses.length - 1;) {
      uint256 amountToTransfer = (amount * ratios[i]) / ROUTE_REQUIRED_RATIO;
      (bool success,) = addresses[i].call{value: amountToTransfer}("");
      if (!success) revert TransferFailed();
      totalTransferred += amountToTransfer;
      unchecked {
        ++i;
      }
    }
    uint256 remainingBalance = amount - totalTransferred;
    (bool success,) = addresses[addresses.length - 1].call{value: remainingBalance}("");
    if (!success) revert TransferFailed();
  }

  /// @notice Sets the max mintPerBlock limit
  function _setMaxMintPerBlock(uint256 _maxMintPerBlock) internal {
    uint256 oldMaxMintPerBlock = maxMintPerBlock;
    maxMintPerBlock = _maxMintPerBlock;
    emit MaxMintPerBlockChanged(oldMaxMintPerBlock, _maxMintPerBlock);
  }

  /// @notice Sets the max redeemPerBlock limit
  function _setMaxRedeemPerBlock(uint256 _maxRedeemPerBlock) internal {
    uint256 oldMaxRedeemPerBlock = maxRedeemPerBlock;
    maxRedeemPerBlock = _maxRedeemPerBlock;
    emit MaxRedeemPerBlockChanged(oldMaxRedeemPerBlock, _maxRedeemPerBlock);
  }

  /// @notice Compute the current domain separator
  /// @return The domain separator for the token
  function _computeDomainSeparator() internal view returns (bytes32) {
    return keccak256(abi.encode(EIP712_DOMAIN, EIP_712_NAME, EIP712_REVISION, block.chainid, address(this)));
  }
}

contracts/AvUSDSilo.sol.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.20;

/* solhint-disable var-name-mixedcase  */

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "../contracts/interfaces/IAvUSDSiloDefinitions.sol";

/**
 * @title AvUSDSilo
 * @notice The Silo allows to store avUSD during the stake cooldown process.
 */
contract AvUSDSilo is IAvUSDSiloDefinitions {
  using SafeERC20 for IERC20;

  address immutable public _STAKING_VAULT;
  IERC20 immutable public _AVUSD;

  error ZeroAddressError();

  constructor(address stakingVault, address avusd) {
    if (stakingVault == address(0) || avusd == address(0)) {
      revert ZeroAddressError();
    }
    _STAKING_VAULT = stakingVault;
    _AVUSD = IERC20(avusd);
  }

  modifier onlyStakingVault() {
    if (msg.sender != _STAKING_VAULT) revert OnlyStakingVault();
    _;
  }

  function withdraw(address to, uint256 amount) external onlyStakingVault {
    _AVUSD.safeTransfer(to, amount);
  }
}

contracts/SingleAdminAccessControl.sol.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/interfaces/IERC5313.sol";
import "./interfaces/ISingleAdminAccessControl.sol";

/**
 * @title SingleAdminAccessControl
 * @notice SingleAdminAccessControl is a contract that provides a single admin role
 * @notice This contract is a simplified alternative to OpenZeppelin's AccessControlDefaultAdminRules
 */
abstract contract SingleAdminAccessControl is IERC5313, ISingleAdminAccessControl, AccessControl {
  address private _currentDefaultAdmin;
  address private _pendingDefaultAdmin;

  modifier notAdmin(bytes32 role) {
    if (role == DEFAULT_ADMIN_ROLE) revert InvalidAdminChange();
    _;
  }

  /// @notice Transfer the admin role to a new address
  /// @notice This can ONLY be executed by the current admin
  /// @param newAdmin address
  function transferAdmin(address newAdmin) external onlyRole(DEFAULT_ADMIN_ROLE) {
    if (newAdmin == msg.sender) revert InvalidAdminChange();
    _pendingDefaultAdmin = newAdmin;
    emit AdminTransferRequested(_currentDefaultAdmin, newAdmin);
  }

  function acceptAdmin() external {
    if (msg.sender != _pendingDefaultAdmin) revert NotPendingAdmin();
    _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
  }

  /// @notice grant a role
  /// @notice can only be executed by the current single admin
  /// @notice admin role cannot be granted externally
  /// @param role bytes32
  /// @param account address
  function grantRole(bytes32 role, address account) public override onlyRole(DEFAULT_ADMIN_ROLE) notAdmin(role) {
    _grantRole(role, account);
  }

  /// @notice revoke a role
  /// @notice can only be executed by the current admin
  /// @notice admin role cannot be revoked
  /// @param role bytes32
  /// @param account address
  function revokeRole(bytes32 role, address account) public override onlyRole(DEFAULT_ADMIN_ROLE) notAdmin(role) {
    _revokeRole(role, account);
  }

  /// @notice renounce the role of msg.sender
  /// @notice admin role cannot be renounced
  /// @param role bytes32
  /// @param account address
  function renounceRole(bytes32 role, address account) public virtual override notAdmin(role) {
    super.renounceRole(role, account);
  }

  /**
   * @dev See {IERC5313-owner}.
   */
  function owner() public view virtual returns (address) {
    return _currentDefaultAdmin;
  }

  /**
   * @notice no way to change admin without removing old admin first
   */
  function _grantRole(bytes32 role, address account) internal override returns (bool) {
    if (role == DEFAULT_ADMIN_ROLE) {
      address _currentDefaultAdmin_ = _currentDefaultAdmin;
      emit AdminTransferred(_currentDefaultAdmin_, account);
      _revokeRole(DEFAULT_ADMIN_ROLE, _currentDefaultAdmin_);
      _currentDefaultAdmin = account;
      delete _pendingDefaultAdmin;
    }
    return super._grantRole(role, account);
  }
}

contracts/StakedAvUSD.sol.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.20;

/* solhint-disable private-vars-leading-underscore */

import "@openzeppelin/contracts/token/ERC20/extensions/ERC4626.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Permit.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

import "./SingleAdminAccessControl.sol";
import "./interfaces/IStakedAvUSD.sol";

/**
 * @title StakedAvUSD
 * @notice The StakedAvUSD contract allows users to stake avUSD tokens and earn a portion of protocol LST and perpetual yield that is allocated
 * to stakers by the AvUSD DAO governance voted yield distribution algorithm.  The algorithm seeks to balance the stability of the protocol by funding
 * the protocol's insurance fund, DAO activities, and rewarding stakers with a portion of the protocol's yield.
 */
contract StakedAvUSD is SingleAdminAccessControl, ReentrancyGuard, ERC20Permit, ERC4626, IStakedAvUSD {
  using SafeERC20 for IERC20;

  /* ------------- CONSTANTS ------------- */
  /// @notice The role that is allowed to distribute rewards to this contract
  bytes32 private constant REWARDER_ROLE = keccak256("REWARDER_ROLE");
  /// @notice The role that is allowed to blacklist and un-blacklist addresses
  bytes32 private constant BLACKLIST_MANAGER_ROLE = keccak256("BLACKLIST_MANAGER_ROLE");
  /// @notice The role which prevents an address to stake
  bytes32 private constant SOFT_RESTRICTED_STAKER_ROLE = keccak256("SOFT_RESTRICTED_STAKER_ROLE");
  /// @notice The role which prevents an address to transfer, stake, or unstake. The owner of the contract can redirect address staking balance if an address is in full restricting mode.
  bytes32 private constant FULL_RESTRICTED_STAKER_ROLE = keccak256("FULL_RESTRICTED_STAKER_ROLE");
  /// @notice The vesting period of lastDistributionAmount over which it increasingly becomes available to stakers
  uint256 private constant VESTING_PERIOD = 8 hours;
  /// @notice Minimum non-zero shares amount to prevent donation attack
  uint256 private constant MIN_SHARES = 1 ether;

  /* ------------- STATE VARIABLES ------------- */

  /// @notice The amount of the last asset distribution from the controller contract into this
  /// contract + any unvested remainder at that time
  uint256 public vestingAmount;

  /// @notice The timestamp of the last asset distribution from the controller contract into this contract
  uint256 public lastDistributionTimestamp;

  /* ------------- MODIFIERS ------------- */

  /// @notice ensure input amount nonzero
  modifier notZero(uint256 amount) {
    if (amount == 0) revert InvalidAmount();
    _;
  }

  /// @notice ensures blacklist target is not owner
  modifier notOwner(address target) {
    if (target == owner()) revert CantBlacklistOwner();
    _;
  }

  /* ------------- CONSTRUCTOR ------------- */

  /**
   * @notice Constructor for StakedAvUSD contract.
   * @param _asset The address of the avUSD token.
   * @param _initialRewarder The address of the initial rewarder.
   * @param _owner The address of the admin role.
   *
   */
  constructor(IERC20 _asset, address _initialRewarder, address _owner)
    ERC20("Staked avUSD", "savUSD")
    ERC4626(_asset)
    ERC20Permit("savUSD")
  {
    if (_owner == address(0) || _initialRewarder == address(0) || address(_asset) == address(0)) {
      revert InvalidZeroAddress();
    }

    _grantRole(REWARDER_ROLE, _initialRewarder);
    _grantRole(DEFAULT_ADMIN_ROLE, _owner);
  }

  /* ------------- EXTERNAL ------------- */

  /**
   * @notice Allows the owner to transfer rewards from the controller contract into this contract.
   * @param amount The amount of rewards to transfer.
   */
  function transferInRewards(uint256 amount) external nonReentrant onlyRole(REWARDER_ROLE) notZero(amount) {
    _updateVestingAmount(amount);
    // transfer assets from rewarder to this contract
    IERC20(asset()).safeTransferFrom(msg.sender, address(this), amount);

    emit RewardsReceived(amount);
  }

  /**
   * @notice Allows the owner (DEFAULT_ADMIN_ROLE) and blacklist managers to blacklist addresses.
   * @param target The address to blacklist.
   * @param isFullBlacklisting Soft or full blacklisting level.
   */
  function addToBlacklist(address target, bool isFullBlacklisting)
    external
    onlyRole(BLACKLIST_MANAGER_ROLE)
    notOwner(target)
  {
    bytes32 role = isFullBlacklisting ? FULL_RESTRICTED_STAKER_ROLE : SOFT_RESTRICTED_STAKER_ROLE;
    _grantRole(role, target);
  }

  /**
   * @notice Allows the owner (DEFAULT_ADMIN_ROLE) and blacklist managers to un-blacklist addresses.
   * @param target The address to un-blacklist.
   * @param isFullBlacklisting Soft or full blacklisting level.
   */
  function removeFromBlacklist(address target, bool isFullBlacklisting) external onlyRole(BLACKLIST_MANAGER_ROLE) {
    bytes32 role = isFullBlacklisting ? FULL_RESTRICTED_STAKER_ROLE : SOFT_RESTRICTED_STAKER_ROLE;
    _revokeRole(role, target);
  }

  /**
   * @notice Allows the owner to rescue tokens accidentally sent to the contract.
   * Note that the owner cannot rescue avUSD tokens because they functionally sit here
   * and belong to stakers but can rescue staked avUSD as they should never actually
   * sit in this contract and a staker may well transfer them here by accident.
   * @param token The token to be rescued.
   * @param amount The amount of tokens to be rescued.
   * @param to Where to send rescued tokens
   */
  function rescueTokens(address token, uint256 amount, address to) external nonReentrant onlyRole(DEFAULT_ADMIN_ROLE) {
    if (address(token) == asset()) revert InvalidToken();
    IERC20(token).safeTransfer(to, amount);
  }

  /**
   * @dev Burns the full restricted user amount and mints to the desired owner address.
   * @param from The address to burn the entire balance, with the FULL_RESTRICTED_STAKER_ROLE
   * @param to The address to mint the entire balance of "from" parameter.
   */
  function redistributeLockedAmount(address from, address to) external nonReentrant onlyRole(DEFAULT_ADMIN_ROLE) {
    if (hasRole(FULL_RESTRICTED_STAKER_ROLE, from) && !hasRole(FULL_RESTRICTED_STAKER_ROLE, to)) {
      uint256 amountToDistribute = balanceOf(from);
      uint256 avusdToVest = previewRedeem(amountToDistribute);
      _burn(from, amountToDistribute);
      // to address of address(0) enables burning
      if (to == address(0)) {
        _updateVestingAmount(avusdToVest);
      } else {
        _mint(to, amountToDistribute);
      }

      emit LockedAmountRedistributed(from, to, amountToDistribute);
    } else {
      revert OperationNotAllowed();
    }
  }

  /* ------------- PUBLIC ------------- */

  /**
   * @notice Returns the amount of avUSD tokens that are vested in the contract.
   */
  function totalAssets() public view override returns (uint256) {
    return IERC20(asset()).balanceOf(address(this)) - getUnvestedAmount();
  }

  /**
   * @notice Returns the amount of avUSD tokens that are unvested in the contract.
   */
  function getUnvestedAmount() public view returns (uint256) {
    uint256 timeSinceLastDistribution = block.timestamp - lastDistributionTimestamp;

    if (timeSinceLastDistribution >= VESTING_PERIOD) {
      return 0;
    }

    uint256 deltaT;
    unchecked {
      deltaT = VESTING_PERIOD - timeSinceLastDistribution;
    }
    return (deltaT * vestingAmount) / VESTING_PERIOD;
  }

  /// @dev Necessary because both ERC20 (from ERC20Permit) and ERC4626 declare decimals()
  function decimals() public pure override(ERC4626, ERC20) returns (uint8) {
    return 18;
  }

  /* ------------- INTERNAL ------------- */

  /// @notice ensures a small non-zero amount of shares does not remain, exposing to donation attack
  function _checkMinShares() internal view {
    uint256 _totalSupply = totalSupply();
    if (_totalSupply > 0 && _totalSupply < MIN_SHARES) revert MinSharesViolation();
  }

  /**
   * @dev Deposit/mint common workflow.
   * @param caller sender of assets
   * @param receiver where to send shares
   * @param assets assets to deposit
   * @param shares shares to mint
   */
  function _deposit(address caller, address receiver, uint256 assets, uint256 shares)
    internal
    override
    nonReentrant
    notZero(assets)
    notZero(shares)
  {
    if (hasRole(SOFT_RESTRICTED_STAKER_ROLE, caller) || hasRole(SOFT_RESTRICTED_STAKER_ROLE, receiver)) {
      revert OperationNotAllowed();
    }
    super._deposit(caller, receiver, assets, shares);
    _checkMinShares();
  }

  /**
   * @dev Withdraw/redeem common workflow.
   * @param caller tx sender
   * @param receiver where to send assets
   * @param _owner where to burn shares from
   * @param assets asset amount to transfer out
   * @param shares shares to burn
   */
  function _withdraw(address caller, address receiver, address _owner, uint256 assets, uint256 shares)
    internal
    override
    nonReentrant
    notZero(assets)
    notZero(shares)
  {
    if (hasRole(FULL_RESTRICTED_STAKER_ROLE, caller) || hasRole(FULL_RESTRICTED_STAKER_ROLE, receiver) || hasRole(FULL_RESTRICTED_STAKER_ROLE, _owner)) {
      revert OperationNotAllowed();
    }

    super._withdraw(caller, receiver, _owner, assets, shares);
    _checkMinShares();
  }

  function _updateVestingAmount(uint256 newVestingAmount) internal {
    if (getUnvestedAmount() > 0) revert StillVesting();

    vestingAmount = newVestingAmount;
    lastDistributionTimestamp = block.timestamp;
  }

  /**
   * @dev Hook that is called before any transfer of tokens. This includes
   * minting and burning. Disables transfers from or to of addresses with the FULL_RESTRICTED_STAKER_ROLE role.
   */

  function _update(address from, address to, uint256 value) internal virtual override {
    if (hasRole(FULL_RESTRICTED_STAKER_ROLE, from) && to != address(0)) {
      revert OperationNotAllowed();
    }
    if (hasRole(FULL_RESTRICTED_STAKER_ROLE, to)) {
      revert OperationNotAllowed();
    }
    super._update(from, to, value);
  }

  /**
   * @dev Remove renounce role access from AccessControl, to prevent users to resign roles.
   */
  function renounceRole(bytes32, address) public virtual override {
    revert OperationNotAllowed();
  }
}

contracts/StakedAvUSDV2.sol.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.20;

/* solhint-disable var-name-mixedcase */

import "./StakedAvUSD.sol";
import "./AvUSDSilo.sol";
import "./interfaces/IStakedAvUSDCooldown.sol";

/**
 * @title StakedAvUSDV2
 * @notice The StakedAvUSDV2 contract allows users to stake avUSD tokens and earn a portion of protocol LST and perpetual yield that is allocated
 * to stakers by the AvUSD DAO governance voted yield distribution algorithm.  The algorithm seeks to balance the stability of the protocol by funding
 * the protocol's insurance fund, DAO activities, and rewarding stakers with a portion of the protocol's yield.
 * @dev If cooldown duration is set to zero, the StakedAvUSDV2 behavior changes to follow ERC4626 standard and disables cooldownShares and cooldownAssets methods. 
*  If cooldown duration is greater than zero, the ERC4626 withdrawal and redeem functions are disabled, breaking the ERC4626 standard, and enabling the cooldownShares and the cooldownAssets functions.
 */
contract StakedAvUSDV2 is IStakedAvUSDCooldown, StakedAvUSD {
  using SafeERC20 for IERC20;

  mapping(address => UserCooldown) public cooldowns;

  AvUSDSilo public immutable silo;

  uint24 public constant MAX_COOLDOWN_DURATION = 90 days;

  uint24 public cooldownDuration;

  /// @notice ensure cooldownDuration is zero
  modifier ensureCooldownOff() {
    if (cooldownDuration != 0) revert OperationNotAllowed();
    _;
  }

  /// @notice ensure cooldownDuration is gt 0
  modifier ensureCooldownOn() {
    if (cooldownDuration == 0) revert OperationNotAllowed();
    _;
  }

  /// @notice Constructor for StakedAvUSDV2 contract.
  /// @param _asset The address of the avUSD token.
  /// @param initialRewarder The address of the initial rewarder.
  /// @param _owner The address of the admin role.
  constructor(IERC20 _asset, address initialRewarder, address _owner) StakedAvUSD(_asset, initialRewarder, _owner) {
    silo = new AvUSDSilo(address(this), address(_asset));
    cooldownDuration = 1 weeks;
  }

  /* ------------- EXTERNAL ------------- */

  /**
   * @dev See {IERC4626-withdraw}.
   */
  function withdraw(uint256 assets, address receiver, address _owner)
    public
    virtual
    override
    ensureCooldownOff
    returns (uint256)
  {
    return super.withdraw(assets, receiver, _owner);
  }

  /**
   * @dev See {IERC4626-redeem}.
   */
  function redeem(uint256 shares, address receiver, address _owner)
    public
    virtual
    override
    ensureCooldownOff
    returns (uint256)
  {
    return super.redeem(shares, receiver, _owner);
  }

  /// @notice Claim the staking amount after the cooldown has finished. The address can only retire the full amount of assets.
  /// @dev unstake can be called after cooldown have been set to 0, to let accounts to be able to claim remaining assets locked at Silo
  /// @param receiver Address to send the assets by the staker
  function unstake(address receiver) external {
    UserCooldown storage userCooldown = cooldowns[msg.sender];
    uint256 assets = userCooldown.underlyingAmount;
    if (assets == 0) {
      return;
    }

    if (block.timestamp >= userCooldown.cooldownEnd || cooldownDuration == 0) {
      userCooldown.cooldownEnd = 0;
      userCooldown.underlyingAmount = 0;

      silo.withdraw(receiver, assets);
    } else {
      revert InvalidCooldown();
    }
  }

  /// @notice redeem assets and starts a cooldown to claim the converted underlying asset
  /// @param assets assets to redeem
  function cooldownAssets(uint256 assets) external ensureCooldownOn returns (uint256 shares) {
    if (assets > maxWithdraw(msg.sender)) revert ExcessiveWithdrawAmount();

    shares = previewWithdraw(assets);

    UserCooldown storage userCooldown = cooldowns[msg.sender];
    userCooldown.cooldownEnd = uint104(block.timestamp) + cooldownDuration;
    userCooldown.underlyingAmount += uint152(assets);

    _withdraw(msg.sender, address(silo), msg.sender, assets, shares);
  }

  /// @notice redeem shares into assets and starts a cooldown to claim the converted underlying asset
  /// @param shares shares to redeem
  function cooldownShares(uint256 shares) external ensureCooldownOn returns (uint256 assets) {
    if (shares > maxRedeem(msg.sender)) revert ExcessiveRedeemAmount();

    assets = previewRedeem(shares);

    UserCooldown storage userCooldown = cooldowns[msg.sender];
    userCooldown.cooldownEnd = uint104(block.timestamp) + cooldownDuration;
    userCooldown.underlyingAmount += uint152(assets);

    _withdraw(msg.sender, address(silo), msg.sender, assets, shares);
  }

  /// @notice Set cooldown duration. If cooldown duration is set to zero, the StakedAvUSDV2 behavior changes to follow ERC4626 standard and disables cooldownShares and cooldownAssets methods. If cooldown duration is greater than zero, the ERC4626 withdrawal and redeem functions are disabled, breaking the ERC4626 standard, and enabling the cooldownShares and the cooldownAssets functions.
  /// @param duration Duration of the cooldown
  function setCooldownDuration(uint24 duration) external onlyRole(DEFAULT_ADMIN_ROLE) {
    if (duration > MAX_COOLDOWN_DURATION) {
      revert InvalidCooldown();
    }

    uint24 previousDuration = cooldownDuration;
    cooldownDuration = duration;
    emit CooldownDurationUpdated(previousDuration, duration);
  }
}

contracts/StakedAvUSDV3.sol.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.20;

import {IERC20} from '@openzeppelin/contracts/token/ERC20/IERC20.sol';
import {StakedAvUSDV2} from './StakedAvUSDV2.sol';

contract StakedAvUSDV3 is StakedAvUSDV2 {
  /// @notice The role that is allowed to redeem shares bypassing the cooldown mechanics
  bytes32 private constant BRIDGE_ROLE = keccak256('BRIDGE_ROLE');

  constructor(IERC20 _asset, address initialRewarder, address _owner) StakedAvUSDV2(_asset, initialRewarder, _owner) {}

  /// @notice redeem shares into assets bypassing the cooldown mechanics
  /// @param shares shares to redeem
  function bridgeRedeem(uint256 shares) external onlyRole(BRIDGE_ROLE) returns (uint256 assets) {
    if (shares > maxRedeem(msg.sender)) revert ExcessiveRedeemAmount();
    assets = previewRedeem(shares);
    _withdraw(msg.sender, msg.sender, msg.sender, assets, shares);
  }
}

